let pad = runtime.objects.pad.getFirstPickedInstance();

if (pad.vectorX() > 0)
{
	runtime.objects.ball.getFirstInstance().deviate(Ball.NormalDeviation, Ball.ClockwiseDeviation);
}

if (pad.vectorX() < 0)
{
	runtime.objects.ball.getFirstInstance().deviate(Ball.NormalDeviation, Ball.CounterClockwiseDeviation);
}

if (pad.vectorX() === 0)
{
	runtime.objects.ball.getFirstInstance().deviate(Ball.SmallDeviation, Math.random() < 0.5 ? Ball.ClockwiseDeviation : Ball.CounterClockwiseDeviation);
}
